import React, {useEffect, useState} from 'react'
import './catFacts.css'

export const CatFacts = () => {
  const [facts, setFacts] = useState([]);

  useEffect( () => {
    console.log(facts)
  },)

  const update = async () => {
    try {
      const newFact = await fetch('https://cat-fact.herokuapp.com/facts/random')
      .then(res => res.json())
      .then(data => (data.text))
    } catch (err) {
      console.error(err);
    }



    return newFact
  }

  return (
    <div>
      <button id='btn' onClick={async () => await setFacts(update)}></button>
      <ul></ul>
    </div>
  )
}
