import React, {useEffect, useState} from 'react'
import './catFacts.css'

export const CatFacts = () => {
  const [facts, setFacts] = useState([]);

  useEffect(() => {
    setFacts(facts => [...facts])
  },[facts])

  const update = async () => {
    try {
      const newFact = await (await fetch('https://cat-fact.herokuapp.com/facts/random')).json();
      const fact = await newFact.text
      return fact;
    } catch (err) {
      console.error(err);
    }
  }

  return (
    <div>
      <button id='btn' onClick={() => setFacts(facts => [...facts, update()])}></button>
      <ul>{facts.map((entry, i) => {
        <li key={i}>{entry}</li>
      })}
      </ul>
    </div>
  )
}
